mapper.select:
select * 
from option_detail o
left join goods_option g
using (option_id)
where option_type = (
	select option_type
	from (
		select * 
		from goods_option
		left join option_detail
		using (option_id)
		where goods_option.p_id = #{pid}
		order by option_type
	) where rownum= 1
) and g.p_id = #{pid}

select * 
from option_detail o
left join goods_option g
using (option_id)
where option_color = (
    select option_color
    from (
        select distinct option_color
        from goods_option
        left join option_detail
        using (option_id)
        where goods_option.p_id =  #{pid} 
        order by option_color asc
    ) where rownum= 1
) and g.p_id =  #{pid}

select * 
from option_detail
left join goods_option
using (option_id)
where p_id = #{goodsId}
and option_type = #{optionType}

select * 
from option_detail
left join goods_option
using (option_id)
where p_id = #{goodsId}
and option_color = #{optionColor}

select * 
from option_detail
left join goods_option
using (option_id)


		p_id = #{goodsId}



		and option_type = #{optionType}



		and option_color = #{optionColor}



		and option_size = #{optionSize}

select * 
from option_detail
left join goods_option
using (option_id)


		p_id = #{goodsId}



		and option_type = #{optionType}



		and option_color = #{optionColor}



		and option_size = #{optionSize}

select
	*
from
	goods_cart
where
	p_id = #{pId}
	and option_id = #{optionId}
	and member_id = #{memberId}

mapper.update:
update
	goods_cart
set
	cart_quantity = cart_quantity + #{cartQty}
where
	cart_id = #{cartId}

mapper.select:
select * 
from goods_cart 
left join goods
using (p_id)
left join option_detail
using (option_id)
where member_id = #{memeberId}
order by cart_id desc

select * 
from goods_cart 
left join goods
using (p_id)
left join option_detail
using (option_id)
where cart_id = #{id}

mapper.update:
update
	goods_cart
set
	cart_quantity = #{qty}
where
	cart_id = #{cartId}

mapper.select:
select * 
from goods_like 
where p_id = #{pId}
and user_id = #{userId}

mapper.delete:
delete from goods_like
where user_id = #{userId}
and p_id = #{pId}

mapper.select:
select *
from goods_order
where member_id = #{memberId}
and total_price = #{totalPrice}
